import React, { StrictMode } from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const skills = [
  {
    skill: "HTML+CSS",
    level: "advanced",
    color: "#2662EA",
  },
  {
    skill: "JavaScript",
    level: "advanced",
    color: "#EFD81D",
  },
  {
    skill: "Web Design",
    level: "advanced",
    color: "#C3DCAF",
  },
  {
    skill: "Git and GitHub",
    level: "intermediate",
    color: "#E84F33",
  },
  {
    skill: "React",
    level: "advanced",
    color: "#60DAFB",
  },
  {
    skill: "Svelte",
    level: "beginner",
    color: "#FF3B00",
  },
];

function App() {
  return (
    <div className="card">
      <Avatar />
      <div className="data">
        <Intro />
        <SkillList />
      </div>
    </div>
  );
}

function Avatar() {
  return <img className="avatar" src="me.jpg" alt="Vishal Kumar Singh" />;
}

function Intro() {
  return (
    <div>
      <h1>Vishal Kumar Singh</h1>
      <p>
        I am a passionate B.Tech student with a strong interest in web
        development and software engineering. Proficient in JavaScript, React,
        and modern web technologies, I enjoy building interactive and
        user-friendly applications. I have experience working with APIs,
        optimizing performance, and creating intuitive user interfaces. Always
        eager to learn and explore new technologies..
      </p>
    </div>
  );
}

function SkillList() {
  const skillsobj = skills;
  return (
    <div className="skill-list">
      {skillsobj.map((skill) => (
        <Skill
          skill={skillsobj.skill}
          level={skillsobj.level}
          color={skillsobj.color}
        />
      ))}
    </div>
  );
}

function Skill({ skill, level, color }) {
  return (
    <div className="skill" style={{ backgroundColor: color }}>
      <span>{skill}</span>
      <span>
        {level === 'begineer' && 'ðŸ‘¶'}
        {level === 'intermediate' && 'ðŸ‘¶'}
        {level === 'begineer' && 'ðŸ‘¶'}

      </span>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
